mapper.select:
		select mno, mname,mid,memail,maddr,maddr_detail,mpoint,mphone,mstate
		from (select /*+index_desc(tbl_member)*/ mno, mname,mid,memail,maddr,maddr_detail,mpoint,mphone,rownum rn ,mstate
              from tbl_member order by rownum asc)
        where rn between #{startRow} and #{endRow}

mapper.update:
update tbl_member set
mstate = 0
where mno in (
	
		#{mnos}
	
)

update tbl_member set
mstate = 1
where mno in (
	
		#{mnos}
	
)

mapper.select:
		select nno,ncategory, ntitle, nregdate,nstate
		from (select /*+index_desc(tbl_product)*/ nno, ncategory,ntitle,nregdate,nstate,rownum rn
		      from tbl_notice order by nregdate asc)
        where rn between #{startRow} and #{endRow}

mapper.insert:
insert into tbl_attr(ano,pno,afilename,apath,auuid,image_info,aurl) SELECT seq_attr_ano.nextval, ano.* FROM(
select #{file.pno} as pno, #{file.afileName} as afileName, #{file.apath} as apath, #{file.auuid} as auuid, #{file.image_info} as image_info, #{file.aurl} as aurl from dual
) ano

insert into tbl_product_option(ono,pno,oname,oprice,otype) SELECT seq_option_ono.nextval, ono.* FROM(
select #{ramList.pno} as pno, #{ramList.oname} as oname, #{ramList.oprice} as oprice, #{ramList.otype} as otype from dual
) ono

mapper.select:
select * from tbl_attr
where pno = #{pno}
and image_info = 'thoumb'

select * from tbl_product_option
where pno = #{pno}
and otype = 1

select * from tbl_product_option
where pno = #{pno}
and otype = 2

select * from tbl_product_option
where pno = #{pno}
and otype = 3

mapper.update:
merge into tbl_product_option op using(
select #{item.ono} as ono, #{item.pno} as pno, #{item.oname} as oname, #{item.oprice} as oprice, #{item.otype} as otype from dual
) add_op on (op.ono = add_op.ono) when matched then update set op.pno = add_op.pno, op.oname = add_op.oname, op.oprice = add_op.oprice, op.otype = add_op.otype when not matched then insert(op.ono,op.pno,op.oname,op.oprice,op.otype) values(seq_option_ono.nextval,add_op.pno,add_op.oname,add_op.oprice,add_op.otype)

merge into tbl_attr a using(
select #{item.ano} as ano, #{item.pno} as pno, #{item.apath} as apath, #{item.auuid} as auuid, #{item.aurl} as aurl, #{item.afileName} as afileName, #{item.image_info} as image_info from dual
)add_attr on(a.ano = add_attr.ano) when matched then update set a.pno = add_attr.pno, a.apath = add_attr.apath, a.auuid = add_attr.auuid, a.aurl = add_attr.aurl, a.afileName = add_attr.afileName, a.image_info = add_attr.image_info

mapper.select:
select * from (select ono, mid, oname, ophone, oprice, oaddr, oaddr_detail, opost_code, odelivery, delivery_status, regdate, rownum rn from tbl_order
	order by rn desc)
	where rn between #{startRow} and #{endRow}

mapper.update:
update tbl_order
set
delivery_status = #{status}
where ono = #{ono}
