mapper.select:
select distinct tbl_product.pno, pname, mno, pcode, pdno, pinfo_main, pimage_thoumb from tbl_product, tbl_product_option where ptype = #{ptype} and oname in
#{option}
and tbl_product.pno = tbl_product_option.pno and isdelete = 0

mapper.update:
update tbl_product
set
order_count = order_count - #{order_count}
where pno = #{pno}
